        private bool Cnd_craft_iron1(MenuCallbackArgs args) { int amount = Campaign.Current.MainParty.ItemRoster.GetItemNumber(DefaultItems.IronOre); if (Olto_Craft_SubModule.no_coal) { MBTextManager.SetTextVariable("STR", "3 (Ore)"); } else { int coal_amount = Campaign.Current.MainParty.ItemRoster.GetItemNumber(DefaultItems.Charcoal); amount = Math.Min(amount, coal_amount); MBTextManager.SetTextVariable("STR", "4 (Ore, coal)"); } MBTextManager.SetTextVariable("AMOUNT", amount);  if (amount >= 1) return true; return false; }
