        public bool GetCnd(string str)
        { int[] ing = GetIng(str); int[] cnt = GetCnt(); var craft = Game.Current.ObjectManager.GetObject<ItemObject>(str); float skills = 0; if (craft.Tierf >= 5) skills = 100; else if (craft.Tierf >= 4) skills = 75; else if (craft.Tierf >= 3) skills = 50; if (Hero.MainHero.GetSkillValue(DefaultSkills.Crafting) < skills) return false; else if (craft.Tierf >= 2) skills = 25; if (cnt[6] >= ing[6] && cnt[5] >= ing[5] && cnt[4] >= ing[4] && cnt[3] >= ing[3] && cnt[2] >= ing[2] && cnt[1] >= ing[1] && cnt[0] >= 1 && Hero.MainHero.Gold >= ing[0]) return true; return false; }
